import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2"; // Only import Pie from react-chartjs-2
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js'; // Import necessary components from chart.js

ChartJS.register(ArcElement, Tooltip, Legend); // Register the ArcElement and other necessary components

const RevenueCheck = () => {
    const [shopRevenue, setShopRevenue] = useState([]);
    const [totalRevenue, setTotalRevenue] = useState(0);

    const checkRevenue = async () => {
        try {
            const response = await fetch('http://localhost:5000/admin/checkrevenue',{
                method: 'GET',
                  credentials: 'include'

            });
    
            if (response.ok) {
                const data = await response.json();
                setTotalRevenue(data.totalRevenue);
                setShopRevenue(data.shopRevenues);
            } else {
                throw new Error("Expected JSON, but received: " );
            }
        } catch (error) {
            console.error("Error fetching revenue data:", error);
        }
    };
    

    useEffect(() => {
        checkRevenue();
    }, []);

    // Prepare data for Pie Chart
    const chartData = {
        labels: shopRevenue.map((shop) => shop.shopName), // Map to shop names
        datasets: [{
            label: 'Shop Revenue',
            data: shopRevenue.map((shop) => shop.platformFee), // Map to platform fee values
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'], // Add more colors if necessary
        }],
    };

    return (
        <div>
            <h1>Total Revenue: {totalRevenue}</h1>
            <h2>Shop Revenue Breakdown:</h2>
            <ul>
                {shopRevenue.map((shop) => (
                    <li key={shop.shopId}>{shop.shopName}: {shop.platformFee}</li>
                ))}
            </ul>
            <h2>Revenue Distribution</h2>
            <Pie data={chartData} />
        </div>
    );
};

export default RevenueCheck;
