import React, { useState } from 'react';
import './BookingModal.css'; // Style your modal accordingly

const BookingModal = ({ availableSlots, selectedDate, onClose, onDateSelect }) => {
  const [selectedTime, setSelectedTime] = useState('');

  const handleSlotSelection = (slot) => {
    setSelectedTime(slot);
    // You can trigger booking function here
  };

  return (
    <div className="bm-modal">
      <div className="bm-modal-content">
        <span className="bm-close" onClick={onClose}>&times;</span>
        <h2>Select a Time Slot</h2>
        <h4>Date: {selectedDate}</h4>
        {availableSlots.length === 0 ? (
          <p>No available slots for this date.</p>
        ) : (
          <ul>
            {availableSlots.map((slot, index) => (
              <li key={index} onClick={() => handleSlotSelection(slot)}>
                {slot.start} - {slot.end}
              </li>
            ))}
          </ul>
        )}
        {selectedTime && <p>Selected Time: {selectedTime}</p>}
      </div>
    </div>
  );
};

export default BookingModal;
