import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css'; // Import the CSS file

const AdminDashboard = () => {
    const [username, setUsername] = useState(null);
    const [users, setUsers] = useState([]);
    const [shops, setShops] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [selectedShop, setSelectedShop] = useState(null);
    const [isUserModalOpen, setIsUserModalOpen] = useState(false);
    const [isShopModalOpen, setIsShopModalOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const checkSession = async () => {
            const response = await fetch('http://localhost:5000/admin/checksession', {
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                setUsername(data.username);
                setUsers(data.details.users);
                setShops(data.details.shops);
            } else {
                navigate('/login');
                setUsername(null);
            }
        };

        checkSession();
    }, [navigate]);

    const openUserModal = (user) => {
        setSelectedUser(user);
        setIsUserModalOpen(true);
    };

    const closeUserModal = () => {
        setIsUserModalOpen(false);
        setSelectedUser(null);
    };

    const openShopModal = (shop) => {
        setSelectedShop(shop);
        setIsShopModalOpen(true);
    };

    const closeShopModal = () => {
        setIsShopModalOpen(false);
        setSelectedShop(null);
    };

    const handleUserChange = (e) => {
        const { name, value } = e.target;
        setSelectedUser({ ...selectedUser, [name]: value });
    };

    const handleShopChange = (e) => {
        const { name, value } = e.target;
        setSelectedShop({ ...selectedShop, [name]: value });
    };

    const handleUserSubmit = async () => {
        // Implement user update logic here
        console.log("Updated User:", selectedUser);
        closeUserModal();
    };

    const handleShopSubmit = async () => {
        // Implement shop update logic here
        console.log("Updated Shop:", selectedShop);
        closeShopModal();
    };

    if (!username) {
        return <div className="login-message">You must be logged in as an admin to view this page.</div>;
    }

    return (
        <div className="admin-dashboard">
            <h1>Welcome, {username}</h1>
            <h2>Users</h2>
            <ul>
                {users.map(user => (
                    <li key={user._id} onClick={() => openUserModal(user)}>
                        <p>Username: {user.username}</p>
                        <p>Email: {user.email}</p>
                    </li>
                ))}
            </ul>
            <h2>Shops</h2>
            <ul>
                {shops.map(shop => (
                    <li key={shop._id} onClick={() => openShopModal(shop)}>
                        <p>Shop Name: {shop.shopname}</p>
                        <p>Shop Owner: {shop.owner}</p>
                        <p>Shop Email: {shop.email}</p>
                    </li>
                ))}
            </ul>

            {isUserModalOpen && (
                <div id="user-modal" className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={closeUserModal}>&times;</span>
                        <h2>Edit User Details</h2>
                        <form>
                            <label>
                                Username:
                                <input
                                    type="text"
                                    name="username"
                                    className="input-field"
                                    value={selectedUser.username}
                                    onChange={handleUserChange}
                                />
                            </label>
                            <label>
                                Email:
                                <input
                                    type="email"
                                    name="email"
                                    className="input-field"
                                    value={selectedUser.email}
                                    onChange={handleUserChange}
                                />
                            </label>
                            <button type="button" className="submit-button" onClick={handleUserSubmit}>Save Changes</button>
                        </form>
                    </div>
                </div>
            )}

            {isShopModalOpen && (
                <div id="shop-modal" className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={closeShopModal}>&times;</span>
                        <h2>Edit Shop Details</h2>
                        <form>
                            <label>
                                Shop Name:
                                <input
                                    type="text"
                                    name="shopname"
                                    className="input-field"
                                    value={selectedShop.shopname}
                                    onChange={handleShopChange}
                                />
                            </label>
                            <label>
                                Shop Owner:
                                <input
                                    type="text"
                                    name="owner"
                                    className="input-field"
                                    value={selectedShop.owner}
                                    onChange={handleShopChange}
                                />
                            </label>
                            <label>
                                Shop Email:
                                <input
                                    type="email"
                                    name="email"
                                    className="input-field"
                                    value={selectedShop.email}
                                    onChange={handleShopChange}
                                />
                            </label>
                            <button type="button" className="submit-button" onClick={handleShopSubmit}>Save Changes</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
