import React, { useEffect, useState, useRef } from 'react';
import VenueCard from './VenueCard';
import './Venue.css';

function Venue() {
  const [venues, setVenues] = useState([]);
  const [loading, setLoading] = useState(true);
  const effectRan = useRef(false); // Add a ref to track effect runs

  const staticVenues = [
    { name: "Praxis Fitness Hub", address: "D.G.S Melkote Park, Nampally", image: "Boxcricket.jpeg" },
    { name: "Dugout Sports Arena", address: "4-231/1, Amar Arcade", image: "Batminton.jpeg" },
    { name: "Vishwamanya Badminton", address: "Railnilayam Road", image: "Cricket.jpg" },
    { name: "HighBall", address: "3-6-363, Street No. 20", image: "Football.jpeg" },
    { name: "Bharathiya Swimming Acadamy", address: "3-6-363, Street No. 20", image: "Swimming.jpg" },
    { name: "Burnley Tennis Club", address: "3-6-363, Street No. 20", image: "Tennis.jpg" }
  ];

  useEffect(() => {
    if (effectRan.current === false) { // Only run the effect once
      const venueload = async () => {
        try {
          const response = await fetch('http://localhost:5000/shop/loadvenue', {
            credentials: 'include'
          });
          if (response.ok) {
            const data = await response.json();
            console.log('Fetched Data:', data);
            if (Array.isArray(data) && data.length > 0) {
              setVenues([...staticVenues, ...data]);
            } else {
              console.warn('Fetched data is not in expected format:', data);
            }
          } else {
            console.error('Failed to load venues:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching venues:', error);
          alert('An error occurred while fetching venues.');
        } finally {
          setLoading(false);
        }
      };
      venueload();
      effectRan.current = true; // Mark the effect as run
    }
    return () => {
      effectRan.current = true; // Cleanup
    };
  }, []); // Empty dependency array

  if (loading) {
    return <div>Loading venues...</div>;
  }

  return (
    <div className="app">
      <div className="book-venues">
        <div className='heading'>
          <h2 className='heading2'>Book Venues</h2>
          <a href="#" className="see-all">SEE ALL VENUES</a>
        </div>
        <div className="venues-container">
          {venues.map((venue, index) => (
            <VenueCard key={index} venue={venue} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Venue;
