import React, { useState } from 'react';
import './SearchSection.css';
import 'boxicons/css/boxicons.min.css';

const SearchSection = () => {
    const [selectedSport, setSelectedSport] = useState('');
    const [nestedDropdownVisible, setNestedDropdownVisible] = useState(false);

    const handleDropdownChange = (event) => {
        const value = event.target.value;
        setSelectedSport(value);
        setNestedDropdownVisible(value !== 'All Sports');
    };

    return (
        <div className="search-section">
            <div>
                <h1 className="search-heading">
                    Book Top Sports Complexes in Hyderabad Online
                </h1>
            </div>

            <div className="search-input">
                <i className='bx bx-search-alt-2'></i>
                <input type="text" placeholder="Search by venue name" />
            </div>

            <div className="dropdown">
                <select onChange={handleDropdownChange} value={selectedSport}>
                    <option value="All Sports">All Sports</option>
                    <option value="Football">Football</option>
                    <option value="Cricket">Cricket</option>
                    <option value="Basketball">Basketball</option>
                    {/* Add more sports as needed */}
                </select>

                {nestedDropdownVisible && (
                    <div className="nested-dropdown">
                        <select>
                            <option>Option 1</option>
                            <option>Option 2</option>
                            <option>Option 3</option>
                            {/* Add more options as needed */}
                        </select>
                    </div>
                )}
            </div>
        </div>
    );
}

export default SearchSection;
