// BlogSection.jsx
import React from 'react';
import BlogCard from './BlogCard';
import './BlogSection.css'; // Add specific styling for BlogSection if needed

const BlogSection = () => {
  const blogs = [
    {
      image: 'image-path1.jpg',
      title: 'Learn Volleyball in 5!',
      description: 'The most simplified Volleyball manual...',
      date: 'SEPTEMBER 26, 2018',
      author: 'PLAYO'
    },
    {
      image: 'image-path2.jpg',
      title: 'Names Celebrated by Cricket!',
      description: 'Understand what it takes to become...',
      date: 'MARCH 3, 2016',
      author: 'SHERYL THOMAS'
    },
    {
      image: 'image-path3.jpg',
      title: 'Easy-to-Learn Badminton Hand Signals',
      description: 'Get a peek of game-basics that’ll ...',
      date: 'AUGUST 2, 2019',
      author: 'NIDHI PATEL'
    },
    {
      image: 'image-path4.jpg',
      title: 'A Spectator’s Tournament Guide',
      description: 'This is how you go prepped for a G...',
      date: 'JANUARY 12, 2022',
      author: 'PLAYO'
    },
    {
      image: 'image-path5.jpg',
      title: 'Take Football Shots Like a Pro!',
      description: 'Learn what it takes to master popula...',
      date: 'APRIL 15, 2017',
      author: 'ARJUN THOMAS'
    }
  ];

  return (
    <div className="blog-section">
      <h1>Blogs to Keep You Fit!</h1>
      <p>Discover exciting blog posts on sports and fitness.</p>
      <div className="blog-container">
        {blogs.map((blog, index) => (
          <BlogCard
            key={index}
            image={blog.image}
            title={blog.title}
            description={blog.description}
            date={blog.date}
            author={blog.author}
          />
        ))}
      </div>
    </div>
  );
};

export default BlogSection;
