import React, { useEffect, useState, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import './BookingPage.css';

const BookingPage = () => {
  const { name } = useParams(); // This captures the `name` parameter from the URL.
  const [venue, setVenue] = useState(null); // Change `venues` to `venue` to represent a single venue.
  const [loading, setLoading] = useState(true);
  const effectRan = useRef(false);

  useEffect(() => {
    if (effectRan.current === false) {
      const loadVenue = async () => {
        try {
          const response = await fetch('http://localhost:5000/shop/loadground', { 
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ name }), // Pass the name parameter from the URL to the backend.
            credentials: 'include'
          });
          
          if (response.ok) {
            const data = await response.json();
            console.log('Fetched Data:', data);
            setVenue(data); // Set the fetched data into the state.
          } else {
            console.error('Failed to load Ground:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching Ground:', error);
          alert('An error occurred while fetching Ground.');
        } finally {
          setLoading(false);
        }
      };

      loadVenue();
      effectRan.current = true;
    }

    return () => {
      effectRan.current = true; // Cleanup to prevent double fetch in strict mode.
    };
  }, [name]); // Add `name` as a dependency to re-fetch data when the URL changes.

  if (loading) {
    return <div>Loading Ground...</div>; // Display loading message while fetching.
  }

  if (!venue) {
    return <div>No Ground Data Available</div>; // Show a message if no data is available.
  }

  return (
    <div className="venue-page">
      <div className="content">
        <VenueDetails
          name={venue.name}
          location={venue.address}
          image={venue.image}
          rating={venue.averagerating} // Use the virtual field 'averagerating' from the schema.
          ratingCount={venue.reviews ? venue.reviews.length : 0}
          timing={venue.availability}
          facilities={venue.facilities}
          pricePerHour={venue.priceperhour}
        />
        <div className="side-info">
          <BookingInfo pricePerHour={venue.priceperhour} />
          <MapLocation address={venue.address} />
        </div>
      </div>
    </div>
  );
};

const VenueDetails = ({ name, location, image, rating, ratingCount, timing, facilities, sports, pricePerHour }) => {
  return (
    <div className="venue-details">
      <h2>{name}</h2>
      <p>{location} · ⭐ {rating} ({ratingCount} ratings) <Link to="#">Rate Venue</Link></p>
      <img src={require(`../assets/${image}`)} alt={name} className="venue-image" />
      <div className="venue-info">
        <h4>Timing</h4>
        <p>{timing.map((slot, index) => (
          <div key={index}>{slot.day}: {slot.times.map((time, i) => (
            <span key={i}>{time.start} - {time.end}</span>
          ))}</div>
        ))}</p>
        <h4>Facilities</h4>
        <ul>
          {facilities.map((facility, index) => (
            <li key={index}>{facility}</li>
          ))}
        </ul>
        <h4>Sports</h4>
        <p>{sports}</p>
        <h4>Price</h4>
        <p>₹{pricePerHour} per hour</p>
      </div>
    </div>
  );
};

const BookingInfo = ({ pricePerHour }) => {
  return (
    <div className="booking-info">
      <h3>Book Now</h3>
      <p>Price: ₹{pricePerHour} per hour</p>
      <button className="book-now">Book Now</button>
      <div className="button-group">
        <button className="share"><i className="fas fa-share-alt"></i> Share</button>
        <button className="bulk-corporate">Bulk / Corporate</button>
      </div>
    </div>
  );
};

export default BookingPage;
