import React, { useEffect, useState, useRef } from "react";

const AddSports = () => {
  const [sportlist, getSportsList] = useState([]);
  const effectRan = useRef(false);

  useEffect(() => {
    const getSports = async () => { 
      try { 
        const res = await fetch("http://localhost:5000/admin/getsportslist", { 
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        });
        const data = await res.json();
        getSportsList(data.sportslist);
      } catch (err) {
        console.log(err);
      }
    };

    if (!effectRan.current) {
      getSports();
      effectRan.current = true;
    }

    return () => {
      effectRan.current = true; // Cleanup
    };
  }, []);

  const addToSports = async (e) => {
    e.preventDefault();

    const form = e.target;
    const sportName = form.sportName.value;
    const equipmentRequired = form.equipmentRequired.value.split(',').map(item => item.trim()); // Split by comma and trim
    const rules = form.rules.value.split(',').map(item => item.trim()); // Split by comma and trim

    const sportData = {
      sportName,
      equipmentRequired,
      rules
    };

    try {
      const res = await fetch("http://localhost:5000/admin/addsport", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(sportData), // Send JSON-encoded data
      });

      if (res.ok) {
        alert("Sport Added Successfully");
        window.location.reload();
      } else {
        alert("Failed to Add Sport");
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      <h1>Add Sport to the Sport List</h1>
      <form onSubmit={addToSports}> 
        <label>Name of the Sport:</label>
        <input type="text" name="sportName" placeholder="Enter the name of sport" required />

        <label>Equipments Required for the Sport (comma-separated):</label>
        <input type="text" name="equipmentRequired" placeholder="Enter the equipment required (comma-separated)" required />

        <label>Rules for the Sport (comma-separated):</label>
        <input type="text" name="rules" placeholder="Enter the rules of sports (comma-separated)" required />

        <button type="submit">Add Sport</button>
      </form>

      <h1>Existing Sports</h1>
      {sportlist.length > 0 ? (
        <ul>
          {sportlist.map((sport) => (
            <li key={sport._id}>
              <h3>Name: {sport.name}</h3>
              <h3>Equipments Required: {sport.equipmentRequired.join(", ")}</h3> {/* Display as comma-separated */}
              <h3>Rules: {sport.rules.join(", ")}</h3> {/* Display as comma-separated */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No Sports have been added yet</p>
      )}
    </div>
  );
};

export default AddSports;
