import './Dashboard.css';
import React, { useEffect, useState } from 'react';
import { useNavigate, Link, Outlet } from 'react-router-dom';

function Dashboard() {
  const [admin, setAdmin] = useState(null);
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalShops, setTotalShops] = useState(0);
  const [totalGrounds, setTotalGrounds] = useState(0);
  const [groundsToVerify, setGroundsToVerify] = useState([]);
  const [appliedGrounds, setAppliedGrounds] = useState([]);
  const [revenuePercentage, setRevenuePercentage] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    const checkSession = async () => {
      try {
        const response = await fetch('http://localhost:5000/admin/checksession', {
          credentials: 'include'
        });

        if (response.ok) {
          const data = await response.json();
          setAdmin(data.user);

          setTotalUsers(data.details?.users?.length || 0);
          setTotalShops(data.details?.shops?.length || 0);

          let totalGroundsCount = 0;
          let allGroundsToVerify = [];
          let allAppliedGrounds = [];

          data.details.shops.forEach(shop => {
            shop.availablesports.forEach(ground => {
              totalGroundsCount++;
              if (ground.appliedforverification && !ground.verify) {
                allGroundsToVerify.push(ground);
              }
              if (ground.appliedforverification) {
                allAppliedGrounds.push(ground);
              }
            });
          });

          setTotalGrounds(totalGroundsCount);
          setGroundsToVerify(allGroundsToVerify);
          setAppliedGrounds(allAppliedGrounds);

          // Set the revenue percentage
          if (data.user && data.user.revenuepercentage) {
            setRevenuePercentage(data.user.revenuepercentage);
          }
        } else {
          navigate('/login');
        }
      } catch (error) {
        console.error("Error fetching session data:", error);
      }
    };

    checkSession();
  }, [navigate]);

  // Function to handle platform percentage submission
  const platformPercentageHandler = async (e) => {
    e.preventDefault(); // Prevent form default behavior

    try {
      const response = await fetch('http://localhost:5000/admin/platformpercentage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          revenuepercentage: revenuePercentage
        }),
        credentials: 'include'
      });

      if (response.ok) {
        alert("Platform percentage updated successfully.");
      } else {
        alert("Failed to update platform percentage.");
      }
    } catch (error) {
      console.error("Error updating platform percentage:", error);
      alert("Error updating platform percentage.");
    }
  };

  return (
    <div className="dashboard3">
      <header className="upperbar">
        <h1><a href='/admindashboard'>Boxplay Admin Panel</a></h1>
        <span className="logout" onClick={() => alert("Logged out")}>
          <i className="fas fa-sign-out-alt"></i> Logout
        </span>
      </header>

      <h1>Welcome Admin {admin ? admin.username : 'Loading...'}</h1>
      <section className="dashboard-summary">
        <div className="summary-item">
          <h2>Total Users</h2>
          <p>{totalUsers}</p>
        </div>
        <div className="summary-item">
          <h2>Total Shops</h2>
          <p>{totalShops}</p>
        </div>
        <div className="summary-item">
          <h2>Total Grounds</h2>
          <p>{totalGrounds}</p>
        </div>
        <div className="summary-item">
          <h2>Grounds to Verify</h2>
          <p>{groundsToVerify.length}</p>
        </div>
        <div className="summary-item">
          <h2>Applied for Verification</h2>
          <p>{appliedGrounds.length}</p>
        </div>
      </section>

      <div>
        <form onSubmit={platformPercentageHandler}>
          <label>Fix Platform Fee Percentage (%):</label>
          <input
            type='number'
            placeholder='Platform fee Percentage'
            value={revenuePercentage}
            onChange={(e) => setRevenuePercentage(e.target.value)}
          />
          <button type="submit">Submit percentage</button>
        </form>
      </div>

      <nav className="navbar1">
        <ul>
          <Link to='ManageUsers'><li>Manage Users</li></Link>
          <Link to='VerifyUsers'><li>Verify Users</li></Link>
          <Link to='ManageShops'><li>Manage Shops</li></Link>
        </ul>
      </nav>

      <main className="content">
        <Outlet />
      </main>
    </div>
  );
}

export default Dashboard;
