import React, { useEffect, useState } from "react";
const Addsports=()=>{
    const [sportlist,getsportslist]=useState([]);
    useEffect(()=>{
        const getsports=async ()=>{
            try{
            const res=await fetch('http://localhost:5000/admin/getsportslist',{
                method:'GET',
                headers:{
                    'Content-Type':'application/json',
                }

            });
            const data=await res.json();
            getsportslist(data.sportslist);
        }catch(err){
            console.log(err);
        }
        }
    })
    return(
        <div>
            <h1>Add Sport to the Sport List</h1>
            <form>
                <label>Name of the Sport:</label>
              <input type="text" placeholder="Enter the name of sport"></input>
              <label>Equipments Required for the Sport:</label>
              <input type="text" placeholder="Enter the equipments required for sports"></input>
              <label>Rules for the sport:</label>
              <input type="text" placeholder="Enter the rules of sports"></input>
            </form>
            <h1>Existing Sports</h1>
             {sportlist.length>0 ?(
                <ul>
                    {sportlist.map((sport)=>{
                        <li key={sport._id}>
                            <h3>Name:{sport.name}</h3>
                            <h3>Equipments Required:{sport.equipmentRequired}</h3>
                            <h3>Rules:{sport.rules}</h3>
                        </li>
                    })}
                </ul>
             ):(<p>No Sports have been Added yet</p>)}
        </div>
    )
} 
export default AddSports;
