import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import './BookingPage.css';

const BookingPage = () => {
  const { name } = useParams(); // `name` parameter from URL
  const [venueData, setVenueData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // State for error handling
  const shopname = name.replace(/-/g, ' ').split('_')[0];
  const effectRan = useRef(false);

  useEffect(() => {
    if (effectRan.current === false) {
      const fetchVenueData = async () => {
        try {
          const response = await fetch('http://localhost:5000/shop/loadground', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name }), // Sending name as the body to match with `shopname_groundname` format
            credentials: 'include',
          });

          if (response.ok) {
            const data = await response.json();
            setVenueData(data); // Setting venue data from response
          } else {
            const errorData = await response.json();
            console.error('Failed to load Ground:', errorData.message);
            setError(errorData.message); // Set error message if response not ok
          }
        } catch (error) {
          console.error('Error fetching Ground:', error);
          setError('An error occurred while fetching Ground.'); // Set error message on catch
        } finally {
          setLoading(false);
        }
      };

      fetchVenueData();
      effectRan.current = true;
    }

    return () => {
      effectRan.current = true; // Cleanup to prevent double fetch in strict mode
    };
  }, [name]);

  if (loading) {
    return <div>Loading Ground...</div>; // Display loading message while fetching
  }

  if (error) {
    return <div>{error}</div>; // Display error message if any
  }

  if (!venueData) {
    return <div>No venue data available.</div>; // Handle case where no data is returned
  }

  return (
    <div className="bp-booking-page">
      <h1>{shopname} - {venueData.groundname}</h1> {/* Display shop name and ground name */}
      <div className="bp-content">
        <VenueDetails
          name={venueData.groundname}
          location={venueData.grounddimensions ? `Length: ${venueData.grounddimensions.length}m, Width: ${venueData.grounddimensions.width}m` : 'No dimensions available'}
          image={venueData.image} // Pass image correctly
          rating={venueData.reviews ? (venueData.reviews.reduce((sum, review) => sum + review.rating, 0) / venueData.reviews.length).toFixed(1) : 'No ratings yet'} // Calculate average rating
          ratingCount={venueData.reviews ? venueData.reviews.length : 0}
          timing={venueData.availability || []} // Provide default empty array if undefined
          facilities={venueData.facilities || []} // Provide default empty array if undefined
          pricePerHour={venueData.priceperhour}
        />
        <div className="bp-side-info">
          <BookingInfo pricePerHour={venueData.priceperhour} />
        </div>
      </div>
    </div>
  );
};

const VenueDetails = ({ name, location, image, rating, ratingCount, timing, facilities, pricePerHour }) => {
  return (
    <div className="bp-venue-details">
      <h2>{name}</h2>
      <p>{location} · ⭐ {rating} ({ratingCount} ratings)</p>
      {image && <img src={image} alt={name} className="bp-venue-image" />} {/* Display image if available */}
      <div className="bp-venue-info">
        <h4>Timing</h4>
        <p>{timing.map(t => `${t.day}: ${t.times.map(time => `${time.start} - ${time.end}`).join(', ')}`).join('; ')}</p>
        <h4>Facilities</h4>
        <ul>
          {facilities.map((facility, index) => (
            <li key={index}>{facility}</li>
          ))}
        </ul>
        <h4>Price</h4>
        <p>₹{pricePerHour} per hour</p>
      </div>
    </div>
  );
};

const BookingInfo = ({ pricePerHour }) => {
  return (
    <div className="bp-booking-info">
      <h3>Book Now</h3>
      <p>Price: ₹{pricePerHour} per hour</p>
      <button className="bp-book-now">Book Now</button>
    </div>
  );
};

export default BookingPage;
