import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2"; // Assuming you are using Chart.js

const RevenueCheck = () => {
    const [shopRevenue, setShopRevenue] = useState([]);
    const [totalRevenue, setTotalRevenue] = useState(0);

    const checkRevenue = async () => {
        try {
            const response = await fetch('https://localhost:5000/admin/checkrevenue');
            const data = await response.json();

            // Calculate total revenue and shop-wise revenue
            let total = 0;
            const shopRevenueMap = {};

            data.forEach((booking) => {
                const shopId = booking.shop; // Adjust based on your data structure
                const amount = booking.amountPaid;

                total += amount; // Sum total revenue
                if (shopRevenueMap[shopId]) {
                    shopRevenueMap[shopId] += amount; // Accumulate revenue for the shop
                } else {
                    shopRevenueMap[shopId] = amount; // Initialize revenue for the shop
                }
            });

            setTotalRevenue(total);
            setShopRevenue(Object.entries(shopRevenueMap)); // Convert to array of [shopId, revenue]
        } catch (error) {
            console.error("Error fetching revenue data:", error);
        }
    };

    useEffect(() => {
        checkRevenue();
    }, []);

    // Prepare data for Pie Chart
    const chartData = {
        labels: shopRevenue.map(([shopId]) => shopId), // Map to shop IDs
        datasets: [{
            label: 'Shop Revenue',
            data: shopRevenue.map(([, revenue]) => revenue), // Map to revenue values
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'], // Add more colors as needed
        }],
    };

    return (
        <div>
            <h1>Total Revenue: {totalRevenue}</h1>
            <h2>Shop Revenue Breakdown:</h2>
            <ul>
                {shopRevenue.map(([shopId, revenue]) => (
                    <li key={shopId}>{shopId}: {revenue}</li>
                ))}
            </ul>
            <h2>Revenue Distribution</h2>
            <Pie data={chartData} />
        </div>
    );
};

export default RevenueCheck;
