import { useEffect, useState } from 'react';
import './Dashboard.css';
import { Link, Outlet, useNavigate } from 'react-router-dom';

function Dashboard() {
  const navigate = useNavigate();
  const [usercount, setusercount] = useState(0);
  const [shopcount, setshopcount] = useState(0);
  const [showCounts, setShowCounts] = useState(true); // state to control visibility of counts

  useEffect(() => {
    const checkSession = async () => {
      const response = await fetch('http://localhost:5000/admin/checksession', {
        credentials: 'include',
      });

      if (response.ok) {
        const data = await response.json();
        setusercount(data.details.users.length);
        setshopcount(data.details.shops.length);
      } else {
        navigate('/login');
      }
    };

    checkSession();
  }, [navigate]);

  const handleLinkClick = () => {
    setShowCounts(false); // hide the counts when a link is clicked
  };

  return (
    <div className="dashboard3">
      <header className="upperbar">
        <Link to={'/admindashboard'}>
          <h1>Boxplay Admin Panel</h1>
        </Link>
        <h1 className="dashboard4">DashBoard</h1>
        <span className="logout" onClick={() => alert('Logged out')}>
          Logout
        </span>
      </header>
      <nav className="navbar1">
        <ul>
          <Link to="ManageUsers" onClick={handleLinkClick}>
            <li>Manage Users</li>
          </Link>
          <Link to="VerifyUsers" onClick={handleLinkClick}>
            <li>Verify Users</li>
          </Link>
          <Link to="ManageShops" onClick={handleLinkClick}>
            <li>Manage Shops</li>
          </Link>
        </ul>
      </nav>
      <main className="content">
    <Outlet />
    {showCounts && (
      <>
        <h1 className="count">Total Users Count: {usercount}</h1>
        <h1 className="count">Total Shops Count: {shopcount}</h1>
      </>
    )}
</main>

    </div>
  );
}

export default Dashboard;
