import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css'; // Import the CSS file

const AdminDashboard = () => {
    const [username, setUsername] = useState(null);
    const [users, setUsers] = useState([]);
    const [shops, setShops] = useState([]);
    const [selectedData, setSelectedData] = useState(null); // State for selected user/shop data
    const [isModalOpen, setIsModalOpen] = useState(false); // State to control the modal visibility
    const navigate = useNavigate();

    useEffect(() => {
        const checkSession = async () => {
            const response = await fetch('http://localhost:5000/admin/checksession', {
                credentials: 'include' // Send cookies with the request
            });

            if (response.ok) {
                const data = await response.json();
                setUsername(data.username); // Set the username if session is valid
                setUsers(data.details.users); // Set the users if session is valid
                setShops(data.details.shops); // Set the shops if session is valid
            } else {
                navigate('/login');
                setUsername(null); // Clear username if session is not valid
            }
        };

        checkSession();
    }, []);

    const openModal = (data) => {
        setSelectedData(data); // Set the selected user/shop data to the modal
        setIsModalOpen(true); // Open the modal
    };

    const closeModal = () => {
        setIsModalOpen(false); // Close the modal
        setSelectedData(null); // Clear the selected data
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSelectedData({ ...selectedData, [name]: value });
    };

    const handleSubmit = async () => {
        // Implement the logic to update the user or shop data on the backend
        // You can send an API request here using fetch or axios

        console.log("Updated Data:", selectedData); // This will log the updated data in the console
        closeModal(); // Close the modal after submission
    };

    if (!username) {
        return <div className="login-message">You must be logged in as an admin to view this page.</div>;
    }

    return (
        <div className="admin-dashboard">
            <h1>Welcome, {username}</h1>
            <h2>Users</h2>
            <ul>
                {users.map(user => (
                    <li key={user._id} onClick={() => openModal(user)}> {/* On click, open the modal with user data */}
                        <p>Username: {user.username}</p>
                        <p>Email: {user.email}</p>
                    </li>
                ))}
            </ul>
            <h2>Shops</h2>
            <ul>
                {shops.map(shop => (
                    <li key={shop._id} onClick={() => openModal(shop)}> {/* On click, open the modal with shop data */}
                        <p>Shop Name: {shop.shopname}</p>
                        <p>Shop Owner: {shop.owner}</p>
                        <p>Shop Email: {shop.email}</p>
                    </li>
                ))}
            </ul>

            {isModalOpen && ( // Modal implementation
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={closeModal}>&times;</span>
                        <h2>Edit {selectedData.username ? 'User' : 'Shop'} Details</h2>
                        <form>
                            {selectedData.username ? (
                                <>
                                    <label>
                                        Username:
                                        <input
                                            type="text"
                                            name="username"
                                            value={selectedData.username}
                                            onChange={handleChange}
                                        />
                                    </label>
                                    <label>
                                        Email:
                                        <input
                                            type="email"
                                            name="email"
                                            value={selectedData.email}
                                            onChange={handleChange}
                                        />
                                    </label>
                                </>
                            ) : (
                                <>
                                    <label>
                                        Shop Name:
                                        <input
                                            type="text"
                                            name="shopname"
                                            value={selectedData.shopname}
                                            onChange={handleChange}
                                        />
                                    </label>
                                    <label>
                                        Shop Owner:
                                        <input
                                            type="text"
                                            name="owner"
                                            value={selectedData.owner}
                                            onChange={handleChange}
                                        />
                                    </label>
                                    <label>
                                        Shop Email:
                                        <input
                                            type="email"
                                            name="email"
                                            value={selectedData.email}
                                            onChange={handleChange}
                                        />
                                    </label>
                                </>
                            )}
                            <button type="button" onClick={handleSubmit}>Save Changes</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
