import React, { useEffect, useState } from 'react';
import VenueCard from './VenueCard';
import './Venue.css';

function Venue() {
  // State to store combined venues data
  const [venues, setVenues] = useState([]);

  // Static venues data
  const staticVenues = [
    {
      name: "Praxis Fitness Hub",
      address: "D.G.S Melkote Park, Nampally",
      image: "Boxcricket.jpeg"
    },
    {
      name: "Dugout Sports Arena",
      address: "4-231/1, Amar Arcade",
      image: "Batminton.jpeg"
    },
    {
      name: "Vishwamanya Badminton",
      address: "Railnilayam Road",
      image: "Cricket.jpg"
    },
    {
      name: "HighBall",
      address: "3-6-363, Street No. 20",
      image: "Football.jpeg"
    },
    {
      name: "Bharathiya Swimming Acadamy",
      address: "3-6-363, Street No. 20",
      image: "Swimming.jpg"
    },
    {
      name: "Burnley Tennis Club",
      address: "3-6-363, Street No. 20",
      image: "Tennis.jpg"
    }
  ];

  useEffect(() => {
    const venueload = async () => {
      try {
        const response = await fetch('http://localhost:5000/shop/loadvenue', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          console.log('Shop Data:', data);

          // Merge static venues with fetched venues data
          setVenues([...staticVenues, ...data]); // Combine static venues and fetched data
        } else {
          console.error('Failed to load venues:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching venues:', error);
        alert('An error occurred while fetching venues.');
      }
    };

    venueload();
  }, []);

  return (
    <div className="app">
      <div className="book-venues">
        <div className='heading'>
          <h2 className='heading2'>Book Venues</h2>
          <a href="#" className="see-all">SEE ALL VENUES</a>
        </div>
        <div className="venues-container">
          {venues.map((venue, index) => (
            <VenueCard key={index} venue={venue} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Venue;
