import React, { useState, useEffect } from 'react';
import './SearchSection.css';
import 'boxicons/css/boxicons.min.css';

const SearchSection = () => {
    const [isOpen, setIsOpen] = useState(false); // State for dropdown toggle
    const [query, setQuery] = useState(""); // State for search query
    const [suggestions, setSuggestions] = useState([]); // State for fetched suggestions
    const [isDropdownVisible, setDropdownVisible] = useState(false); // State for suggestion dropdown visibility

    // Fetch suggestions from the backend
    const fetchSuggestions = async (searchText) => {
        try {
            const response = await fetch(`/api/venues?search=${searchText}`);
            const data = await response.json();
            setSuggestions(data); // Set the suggestions from backend response
        } catch (error) {
            console.error("Error fetching suggestions:", error);
        }
    };

    // Handle search input changes
    const handleInput = (e) => {
        const value = e.target.value;
        setQuery(value);

        if (value.trim()) {
            fetchSuggestions(value);
            setDropdownVisible(true);
        } else {
            setDropdownVisible(false);
        }
    };

    // Handle suggestion click
    const handleSuggestionClick = (suggestion) => {
        setQuery(suggestion);
        setDropdownVisible(false);
    };

    // Toggle the sport dropdown menu
    const toggleDropdown = () => setIsOpen((prev) => !prev);

    // Handle click outside to close suggestion dropdown
    useEffect(() => {
        const handleClickOutside = () => setDropdownVisible(false);
        window.addEventListener("click", handleClickOutside);
        return () => window.removeEventListener("click", handleClickOutside);
    }, []);

    return (
        <div className="search-dropdown-section">
            <h1 className="search-dropdown-heading">
                Book Top Sports Complexes in Hyderabad Online
            </h1>

            <div className="search-dropdown-input">
                <i className='bx bx-search-alt-2'></i>
                <input
                    type="text"
                    value={query}
                    onChange={handleInput}
                    onClick={(e) => e.stopPropagation()} // Prevent dropdown close on input click
                    placeholder="Search by venue name"
                />
                {isDropdownVisible && suggestions.length > 0 && (
                    <div className="search-dropdown-suggestions">
                        {suggestions.map((suggestion, index) => (
                            <div
                                key={index}
                                className="search-dropdown-suggestion-item"
                                onClick={() => handleSuggestionClick(suggestion)}
                            >
                                {suggestion}
                            </div>
                        ))}
                    </div>
                )}
            </div>

            <div className="search-dropdown-menu">
                <button className="search-dropdown-button" onClick={toggleDropdown}>
                    <i className='bx bx-cricket-ball'></i>
                    Select Sport
                </button>
                {isOpen && (
                    <div className="search-dropdown-content">
                        <div className="search-dropdown-item">All Sports</div>
                        <div className="search-dropdown-item">
                            <a href='/footballvenue'>Football</a>
                        </div>
                        <div className="search-dropdown-item">
                            <a href='/cricketvenue'>Cricket</a>
                        </div>
                        <div className="search-dropdown-item">Basketball</div>
                        <div className="search-dropdown-item">Tennis</div>
                        <div className="search-dropdown-item">Badminton</div>
                        <div className="search-dropdown-item">Volleyball</div>
                        <div className="search-dropdown-item">Baseball</div>
                        <div className="search-dropdown-item">Hockey</div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SearchSection;
