import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import './BookingPage.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const BookingPage = () => {
  const { name } = useParams();
  const [venueData, setVenueData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [playDuration, setPlayDuration] = useState(1); // Default 1 hour
  const [availableDays, setAvailableDays] = useState([]);
  const [priceperhour, setPricePerHour] = useState(0);
  const [availableTimeSlots, setAvailableTimeSlots] = useState([]);
  const shopname = name.replace(/-/g, ' ').split('_')[0];
  const effectRan = useRef(false);
  const [address, setAddress] = useState('');

  useEffect(() => {
    if (effectRan.current === false) {
      const fetchVenueData = async () => {
        try {
          const response = await fetch('http://localhost:5000/shop/loadground', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name }),
            credentials: 'include',
          });

          if (response.ok) {
            const data = await response.json();
            setVenueData(data.ground);
            setAddress(data.address);
            setPricePerHour(data.ground.priceperhour);
            extractAvailableDays(data.ground.availability);
          } else {
            const errorData = await response.json();
            setError(errorData.message);
          }
        } catch (error) {
          setError('An error occurred while fetching Ground.');
        } finally {
          setLoading(false);
        }
      };

      fetchVenueData();
      effectRan.current = true;
    }

    return () => {
      effectRan.current = true;
    };
  }, [name]);

  const extractAvailableDays = (availability) => {
    const days = availability.map(timing => timing.day);
    setAvailableDays(days);
  };

  const openBookingModal = () => {
    setIsModalOpen(true);
  };

  const closeBookingModal = () => {
    setIsModalOpen(false);
    setSelectedDate('');
    setSelectedTime('');
    setAvailableTimeSlots([]);
    setPlayDuration(1);
  };

  const handleDateSelection = (date) => {
    setSelectedDate(date);
    fetchAvailableTimeSlots(date);
  };

  const generateTimeSlots = (start, end) => {
    const startTime = convertTimeStringToMinutes(start);
    const endTime = convertTimeStringToMinutes(end);
    const slots = [];

    for (let time = startTime; time + 30 <= endTime; time += 30) {
      const slotStart = convertMinutesToTimeString(time);
      slots.push(slotStart);
    }

    return slots;
  };

  const fetchAvailableTimeSlots = (date) => {
    const selectedDay = new Date(date).toLocaleString('en-US', { weekday: 'long' });
    const selectedDayAvailability = venueData.availability.find(
      (availability) => availability.day === selectedDay
    );

    if (selectedDayAvailability) {
      const slots = [];
      selectedDayAvailability.times.forEach((timeSlot) => {
        const generatedSlots = generateTimeSlots(timeSlot.start, timeSlot.end);
        slots.push(...generatedSlots);
      });
      adjustSlotsForDuration(slots, selectedDayAvailability.times);
    } else {
      setAvailableTimeSlots([]);
    }
  };

  const adjustSlotsForDuration = (slots, times) => {
    const closingTime = '8:00 PM';
    const closingTimeMinutes = convertTimeStringToMinutes(closingTime);
    const maxStartTime = closingTimeMinutes - (playDuration * 60);

    const filteredSlots = slots.filter((slot) => {
      const slotMinutes = convertTimeStringToMinutes(slot);
      return slotMinutes <= maxStartTime;
    });

    setAvailableTimeSlots(filteredSlots);
  };

  const handleTimeSelection = (time) => {
    setSelectedTime(time);
  };

  const increaseDuration = () => {
    if (playDuration < 4) {
      setPlayDuration(prev => prev + 0.5); // Increase duration by 0.5 hours
      fetchAvailableTimeSlots(selectedDate); // Refetch time slots
    }
  };

  const decreaseDuration = () => {
    if (playDuration > 1) {
      setPlayDuration(prev => prev - 0.5); // Decrease duration by 0.5 hours
      fetchAvailableTimeSlots(selectedDate); // Refetch time slots
    }
  };

  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  const isDateAvailable = (date) => {
    const day = new Date(date).toLocaleString('en-US', { weekday: 'long' });
    return availableDays.includes(day);
  };

  const isDisabled = (date) => {
    return !isDateAvailable(date);
  };

  const convertTimeStringToMinutes = (timeString) => {
    const [time, modifier] = timeString.split(' ');
    let [hours, minutes] = time.split(':');
    if (modifier === 'PM' && hours !== '12') {
      hours = parseInt(hours) + 12;
    }
    if (modifier === 'AM' && hours === '12') {
      hours = '00';
    }
    return parseInt(hours) * 60 + parseInt(minutes);
  };

  const convertMinutesToTimeString = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    const modifier = hours >= 12 ? 'PM' : 'AM';
    const formattedHours = hours % 12 === 0 ? 12 : hours % 12;
    const formattedMinutes = mins.toString().padStart(2, '0');
    return `${formattedHours}:${formattedMinutes} ${modifier}`;
  };

  const handleBookNow = async () => {
    const bookingDetails = {
      date: selectedDate,
      time: selectedTime,
      duration: playDuration,
      venue: venueData.groundname,
      address: address,
      price: priceperhour * playDuration,
    };

    try {
      const response = await fetch('http://localhost:5000/shop/bookground', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bookingDetails),
        credentials: 'include',
      });

      if (response.ok) {
        const result = await response.json();
        alert(`Booking confirmed! ${result.message}`);
        closeBookingModal();
      } else {
        const errorData = await response.json();
        alert(`Booking failed: ${errorData.message}`);
      }
    } catch (error) {
      alert('An error occurred while booking the ground.');
    }
  };

  if (loading) {
    return <div className="bp-loading">Loading Ground...</div>;
  }

  if (error) {
    return <div className="bp-error">{error}</div>;
  }

  if (!venueData) {
    return <div className="bp-no-data">No venue data available.</div>;
  }

  return (
    <div className="bp-booking-page">
      <h1 className="bp-main-title">{shopname} - {venueData.groundname}</h1>
      <div className="bp-content">
        <VenueDetails
          name={venueData.groundname}
          dimensions={venueData.grounddimensions ? `Length: ${venueData.grounddimensions.length}m, Width: ${venueData.grounddimensions.width}m` : 'No dimensions available'}
          address={address ? `Address: ${address}` : 'Address not Available'}
          image={venueData.image}
          rating={venueData.reviews ? (venueData.reviews.reduce((sum, review) => sum + review.rating, 0) / venueData.reviews.length).toFixed(1) : 'No ratings yet'}
          ratingCount={venueData.reviews ? venueData.reviews.length : 0}
          timing={venueData.availability || []}
          facilities={venueData.facilities || []}
          pricePerHour={venueData.priceperhour}
        />
        <div className="bp-side-info">
          <BookingInfo pricePerHour={venueData.priceperhour} onBookNow={openBookingModal} />
        </div>
      </div>

      {isModalOpen && (
        <div className="bp-modal">
          <div className="bp-modal-content">
            <span className="bp-close" onClick={closeBookingModal}>&times;</span>
            <h2 className="bp-modal-title">Book {venueData.groundname}</h2>
            <label className="bp-date-label">Select Date:</label>
            <DatePicker
              selected={selectedDate}
              onChange={handleDateSelection}
              minDate={new Date()}
              filterDate={isDisabled}
              placeholderText="Select a date"
              className="bp-date-picker"
            />
            <div className="bp-time-selection">
              <label className="bp-time-label">Available Time Slots:</label>
              <ul className="bp-time-list">
                {availableTimeSlots.map((time, index) => (
                  <li key={index} onClick={() => handleTimeSelection(time)} className="bp-time-slot">
                    {time}
                  </li>
                ))}
              </ul>
            </div>
            <div className="bp-duration-selection">
              <label className="bp-duration-label">Select Duration:</label>
              <div className="bp-duration-buttons">
                <button onClick={decreaseDuration}>-</button>
                <span>{playDuration} hour{playDuration > 1 ? 's' : ''}</span>
                <button onClick={increaseDuration}>+</button>
              </div>
            </div>
            <button onClick={handleBookNow} className="bp-book-button">Confirm Booking</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingPage;

const VenueDetails = ({ name, dimensions, address, image, rating, ratingCount, timing, facilities, pricePerHour }) => {
  return (
    <div className="bp-venue-details">
      <img src={image} alt={name} className="bp-venue-image" />
      <h2 className="bp-venue-name">{name}</h2>
      <p className="bp-venue-dimensions">{dimensions}</p>
      <p className="bp-venue-address">{address}</p>
      <p className="bp-venue-price">Price: ₹{pricePerHour}/hour</p>
      <p className="bp-venue-rating">Rating: {rating} ({ratingCount} reviews)</p>
      <p className="bp-venue-timing">Available Timing: {timing.map(t => `${t.day}: ${t.start} to ${t.end}`).join(', ')}</p>
      <p className="bp-venue-facilities">Facilities: {facilities.join(', ')}</p>
    </div>
  );
};

const BookingInfo = ({ pricePerHour, onBookNow }) => {
  return (
    <div className="bp-booking-info">
      <h2 className="bp-info-title">Booking Information</h2>
      <p className="bp-info-price">Price: ₹{pricePerHour}/hour</p>
      <button onClick={onBookNow} className="bp-book-button">Book Now</button>
    </div>
  );
};
