import { useEffect, useState } from "react";
import React from "react";
import "./ShopDashboard.css";
import { useNavigate } from 'react-router-dom';

const ShopDashboard = () => {
    const [state, setState] = useState({});
    const [daysPerWeek, setDaysPerWeek] = useState(0); // Track number of availability days per week
    const [daysArray, setDaysArray] = useState([]); // Track selected days
    const [timesArray, setTimesArray] = useState([]); // Track start and end times for each day
    const [grounds, setGrounds] = useState([]); // Store existing grounds
    const navigate = useNavigate();

    useEffect(() => {
        const checkShopSession = async () => {
            try {
                const response = await fetch('http://localhost:5000/shop/checkshopsession', {
                    credentials: 'include'
                });
                if (!response.ok) {
                    navigate('/shoplogin');
                } else {
                    const data = await response.json();
                    setState(data.shop);
                    setGrounds(data.shop.grounds || []); // Initialize with existing grounds
                }
            } catch (error) {
                console.error('Error fetching shop session:', error);
                alert('An error occurred while fetching shop session.');
            }
        };

        checkShopSession();
    }, [navigate]);

    const updatesubmit = async (e) => {
        e.preventDefault();
        const { shopname, address } = e.target.elements;

        try {
            const response = await fetch('http://localhost:5000/shop/updateshop', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    shopname: shopname.value,
                    address: address.value
                }),
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                setState(data.updatedShop); // Update state with new shop details
                alert('Shop details updated successfully');
            } else {
                const error = await response.json();
                alert(`Update failed: ${error.msg}`);
            }
        } catch (error) {
            console.error('Error updating shop details:', error);
            alert('An error occurred while updating shop details.');
        }
    };

    const addGround = async (e) => {
        e.preventDefault();
        const form = e.target;
        const formData = new FormData(form);

        // Construct availability array based on daysArray and timesArray
        const availability = daysArray.map((day, index) => ({
            day,
            times: [{ start: timesArray[index].start, end: timesArray[index].end }]
        }));

        // Append availability as a JSON string
        formData.append('availability', JSON.stringify(availability));

        try {
            const response = await fetch('http://localhost:5000/shop/addground', {
                method: 'POST',
                body: formData, // Automatically sets content-type to multipart/form-data
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                alert('Ground added successfully!');
                setGrounds([...grounds, data.newGround]); // Update grounds list with the new ground
                form.reset(); // Clear form fields or update state as needed
                // Reset state for dynamic form fields
                setDaysPerWeek(0);
                setDaysArray([]);
                setTimesArray([]);
            } else {
                const error = await response.json();
                alert(`Error adding ground: ${error.msg}`);
            }
        } catch (error) {
            console.error('Error adding ground:', error);
            alert('An error occurred while adding the ground.');
        }
    };

    // Handle changes to daysPerWeek and dynamically update daysArray
    const handleDaysPerWeekChange = (e) => {
        const value = parseInt(e.target.value);
        setDaysPerWeek(value);
        setDaysArray(Array.from({ length: value }, () => '')); // Initialize with empty values
        setTimesArray(Array.from({ length: value }, () => ({ start: '', end: '' }))); // Initialize time array
    };

    // Handle changes to individual day selectors
    const handleDayChange = (index, newValue) => {
        const updatedDaysArray = [...daysArray];
        updatedDaysArray[index] = newValue;
        setDaysArray(updatedDaysArray);
    };

    // Handle changes to individual time inputs
    const handleTimeChange = (index, field, value) => {
        const updatedTimesArray = [...timesArray];
        updatedTimesArray[index] = { ...updatedTimesArray[index], [field]: value };
        setTimesArray(updatedTimesArray);
    };

    // Generate options for the day dropdown
    const generateDayOptions = () => {
        const allDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
        const selectedDays = daysArray.filter(day => day !== ''); // Get only selected days
        return allDays.map((day) => (
            <option
                key={day}
                value={day}
                disabled={selectedDays.includes(day)} // Disable if the day is already selected
            >
                {day}
            </option>
        ));
    };

    return (
        <div className="shop-dashboard">
            <h1>Shop Dashboard</h1>
            <h3>Email: {state.email}</h3>
            <h3>Owner Name: {state.owner}</h3>
            
            <form onSubmit={updatesubmit}>
                <label htmlFor="shopname">Shop Name:</label>
                <input
                    type="text"
                    name="shopname"
                    id="shopname"
                    placeholder="Shop Name"
                    defaultValue={state.shopname}
                    required
                />
                <label htmlFor="address">Address:</label>
                <input
                    type="text"
                    name="address"
                    id="address"
                    placeholder="Address"
                    defaultValue={state.address}
                    required
                />
                <button type="submit">Update Shop Details</button>
            </form>
            
            <form onSubmit={addGround}>
                <label htmlFor="groundname">Ground Name:</label>
                <input type="text" name="groundname" id="groundname" placeholder="Ground Name" required />

                <label htmlFor="priceperhour">Price Per Hour:</label>
                <input type="number" name="priceperhour" id="priceperhour" placeholder="Price Per Hour" required />

                <label htmlFor="maxplayers">Maximum Number of Players:</label>
                <input type="number" name="maxplayers" id="maxplayers" placeholder="Maximum Number Of Players" required />

                <label htmlFor="groundLength">Ground Length (meters):</label>
                <input type="number" name="groundLength" id="groundLength" placeholder="Ground Length (meters)" required />

                <label htmlFor="groundwidth">Ground Width (meters):</label>
                <input type="number" name="groundwidth" id="groundwidth" placeholder="Ground Width (meters)" required />

                <label htmlFor="facilities">Facilities Available:</label>
                <input type="text" name="facilities" id="facilities" placeholder="Facilities Available" required />

                <label htmlFor="surfaceType">Surface Type:</label>
                <select name="surfaceType" id="surfaceType" required>
                    <option value="Grass">Grass</option>
                    <option value="Turf">Turf</option>
                    <option value="Clay">Clay</option>
                    <option value="Hard">Hard</option>
                    <option value="Synthetic">Synthetic</option>
                </select>

                <label htmlFor="daysperweek">Days Available Per Week:</label>
                <input
                    type="number"
                    name="daysperweek"
                    id="daysperweek"
                    placeholder="Days Per Week"
                    required
                    value={daysPerWeek}
                    onChange={handleDaysPerWeekChange}
                />

                {Array.from({ length: daysPerWeek }).map((_, index) => (
                    <div key={index}>
                        <label htmlFor={`day-${index}`}>Select Day {index + 1}:</label>
                        <select
                            name={`day-${index}`}
                            id={`day-${index}`}
                            value={daysArray[index]}
                            onChange={(e) => handleDayChange(index, e.target.value)}
                            required
                        >
                            <option value="">Select Day</option>
                            {generateDayOptions()}
                        </select>
                        <label htmlFor={`start-time-${index}`}>Start Time:</label>
                        <input
                            type="time"
                            name={`start-time-${index}`}
                            id={`start-time-${index}`}
                            value={timesArray[index].start}
                            onChange={(e) => handleTimeChange(index, 'start', e.target.value)}
                            required
                        />
                        <label htmlFor={`end-time-${index}`}>End Time:</label>
                        <input
                            type="time"
                            name={`end-time-${index}`}
                            id={`end-time-${index}`}
                            value={timesArray[index].end}
                            onChange={(e) => handleTimeChange(index, 'end', e.target.value)}
                            required
                        />
                    </div>
                ))}

                <button type="submit">Add Ground</button>
            </form>

            <h2>Existing Grounds</h2>
            <ul>
                {grounds.map((ground, index) => (
                    <li key={index}>{ground.name} - {ground.availability.map(a => `${a.day}: ${a.times[0].start} to ${a.times[0].end}`).join(', ')}</li>
                ))}
            </ul>
        </div>
    );
};

export default ShopDashboard;
